def rearrange(mylist): 
    mylist.sort() 
    length = len(mylist) 
    temp = [0]*(length + 1) 

    # to store indexes of mylist[] 
    j, k = 0, length-1;

    # filling temp[] from both sides 
    for i in range(length): 

        if i%2==0: 
            temp[i] = mylist[j] 
            j += 1
        else: 
            temp[i] = mylist[k] 
            k -= 1

    # modify original array 
    for i in range(length): 
        mylist[i] = temp[i]
    return mylist

test_cases:
