def max_depth(root):
    if root == None: 
        return 0;  
    else : 
        #Compute the depth of each subtree
        l_depth = max_depth(root.left) 
        r_depth = max_depth(root.right) 

        #Use the larger one
        if (l_depth > r_depth): 
            return l_depth+1
        else: 
            return r_depth+1

