def top_three_words(string):
    words = string.split()
    word_dict = {}
    for word in words:
        if word in word_dict:
            word_dict[word] +=1
        else:
            word_dict[word] = 1
    sorted_dict = sorted(word_dict.items(), key = lambda x: x[1], reverse = True)
    top_three = [word[0] for word in sorted_dict[:3]]
