def quicksort(arr):
    if len(arr) == 0 or len(arr) == 1:
        return arr
    else:
        pivot = arr[0]
        arr.remove(arr[0])
        left_arr = []
        right_arr = []
        for element in arr:
            if element <= pivot:
                left_arr.append(element)
            else:
                right_arr.append(element)
        left_arr = quicksort(left_arr)
        right_arr = quicksort(right_arr)
        sorted_arr = left_arr + [pivot] + right_arr
        return sorted_arr

