def get_next_fibonacci_number(n):
  if n == 0 or n == 1:
    return n
  else:
    fibonacci_sequence = [0,1] 
    for i in range(2, n + 1): 
      next_value = fibonacci_sequence[i-2] + fibonacci_sequence[i-1] 
      fibonacci_sequence.append(next_value)
    return fibonacci_sequence[n]

