def binary_search(arr, target):
  low = 0
  high = len(arr) - 1

  while low <= high: 
    mid = (low + high) // 2
    if arr[mid] == target: 
      return mid 
    elif arr[mid] < target: 
      low = mid + 1
    else: 
      high = mid - 1
  return -1

def longestPalindrome(st): 
    palindromes = []
    for i in range(len(st)): 
        for j in range(i, len(st)): 
            curr = st[i:j+1] 
            rev = curr[::-1] 
            if (curr == rev): 
                palindromes.append(curr) 
    res = max(palindromes, key = len) 

    return res

