def is_anagram(a, b):
    char_to_count = {}

    if len(a) != len(b):
        return False

    for char in a:
        if char in char_to_count:
            char_to_count[char] += 1
        else:
            char_to_count[char] = 1

    for char in b:
        if char not in char_to_count:
            return False
        else:
            char_to_count[char] -= 1

    for count in char_to_count.values():
        if count != 0:
            return False

    return True

