def find_triplet_sum(arr, target):
    arr.sort()
    for i in range(0, len(arr)-2):
        left = i + 1
        right = len(arr) - 1
        while left < right:
            curr_sum = arr[i] + arr[left] + arr[right]

            if curr_sum == target:
                return True 
            elif curr_sum < target:
                left += 1
            else:
                right -= 1
    return False

