def calculate_isbn(authorname, title, publisher, yearpublished):
    authorname_string = authorname.replace(' ', '')
    title_string = title.replace(' ', '')
    publisher_string = publisher.replace(' ', '')

    isbn_total = 0

    for letter in authorname_string:
        isbn_total += ord(letter)

    for letter in title_string:
        isbn_total += ord(letter)

    for letter in publisher_string:
        isbn_total += ord(letter) 

    isbn_total += yearpublished

    while isbn_total > 9999:
        isbn_total = isbn_total % 10000

    isbn_string = str(isbn_total).zfill(5)

    return isbn_string

