def permutation(str):
    if len(str) == 0:
        return []
    if len(str) == 1:
        return [str]
    lst = [] 
    for i in range(len(str)):
       m = str[i] 
       remLst = permutation(str[:i] + str[i+1:])
       for p in remLst:
           lst.append([m] + p)
    return lst

