def binary_search(data, search_num):
 low = 0
 high = len(data) - 1
 while low <= high:
  mid = (low + high)//2
  if data[mid] == search_num:
   return mid
  elif data[mid] < search_num:
   low = mid + 1
  else:
   high = mid - 1
 return -1

