def is_valid_ipv4(string):
    # Split the string at the '.'s
    parts = string.split(".")

    # Check if the length of the parts is valid
    if len(parts) != 4:
            return False

    # Iterate over each part and check if it is a valid number
    for part in parts:
        if not part.isdigit() or int(part) > 255 or int(part) < 0:
            return False

    return True

