def is_anagram(str1, str2):
    char_map1 = {}
    char_map2 = {}

    for char in str1:
        if char in char_map1:
            char_map1[char] += 1
        else:
            char_map1[char] = 1

    for char in str2:
        if char in char_map2:
            char_map2[char] += 1
        else:
            char_map2[char] = 1

    return char_map1 == char_map2

