def encrypt_caesar_cipher(message, key):
    encrypted_message = ""
    for char in message: 
        if not char.isalpha():
            encrypted_message += char
        else:
            encrypted_char = chr((ord(char) + key - 97) % 26 + 97)
            encrypted_message += encrypted_char
    return encrypted_message

