def three_most_common(lst):
    """This function takes in an array and returns an array of the three most common elements."""
    lst_len = len(lst)
    lst_dic = dict()

    for n in lst:
        if n in lst_dic:
            lst_dic[n] += 1
        else:
            lst_dic[n] = 1

    max_val = 0
    max_key = 0
    sec_key = 0
    last_key = 0

    for key, val in lst_dic.items():
        if val > max_val:
            max_val = val
            last_key = sec_key
            sec_key = max_key
            max_key = key
        elif val > sec_key:
            last_key = sec_key
            sec_key = key
        elif val > last_key:
            last_key = key
    return [max_key, sec_key, last_key]

