def combinations(n, k):
    if k == 0 or k > n:
        return []
    result = []
    def dfs(start,n,k):
        if k == 0:
            result.append(temp[:])
            return 
        for i in range(start, n):
            temp.append(i)
            dfs(i+1, n, k-1)
            temp.pop()

    temp = []
    dfs(0, n, k)
    return result

